import React, { useState } from 'react';
import Select from 'react-select';
import axios from 'axios';
import './Main.css';

function Main() {
    const [length, setLength] = useState({ value: "Select", label: "Select" });
    const [tone, setTone] = useState({ value: "Select", label: "Select" });
    const [features, setFeatures] = useState("");
    const [brandPos, setBrandPos] = useState("");
    const [res, setRes] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const lengthOptions = [
        { value: "Short", label: "Short" },
        { value: "Medium", label: "Medium" },
        { value: "Long", label: "Long" }
    ];

    const toneOptions = [
        { value: "Casual", label: "Casual" },
        { value: "Formal", label: "Formal" },
        { value: "Grandiose", label: "Grandiose" }
    ];

    const generateHandler = async () => {
        setIsLoading(true);
        const data = {
            features: features.split(',').map(item => item.trim()),
            tone: tone.value,
            length: length.value,
            brand_positioning: brandPos
        };

        try {
            const response = await axios.post("http://localhost:8000/generate", data);
            setRes(response.data.generated_copy);
            setIsLoading(false);
        } catch (error) {
            console.error(error);
            setIsLoading(false);
        }
    };

    return (
        <div className="App">
            <div className="container">
                <div className="sub-container">
                    <div className="element">
                        <span>Brand Positioning</span>
                        <input value={brandPos} onChange={(e) => setBrandPos(e.target.value)} style={{ padding: "10px" }} placeholder="text" />
                    </div>
                    <div className="element">
                        <span>Features</span>
                        <input value={features} onChange={(e) => setFeatures(e.target.value)} style={{ padding: "10px" }} placeholder="text" />
                    </div>
                </div>
                <div className="sub-container">
                    <div className="element">
                        <span>Tone</span>
                        <Select options={toneOptions} value={tone} onChange={(val) => setTone(val)} />
                    </div>
                    <div className="element">
                        <span>Length</span>
                        <Select options={lengthOptions} value={length} onChange={(val) => setLength(val)} />
                    </div>
                </div>
                <div className="btn">
                    <button onClick={generateHandler}>Generate</button>
                </div>
                <div className="output">
                    <span>Output</span>
                    {isLoading ? <span>Loading...</span> : <p>{res}</p>}
                </div>
            </div>
        </div>
    );
}

export default Main;
